import React from "react";
import { AddItemsContainer, AddItemsTitle } from "./add-items-form.styles";
import FormInput from "../form-input/form-input";
import CustomButton from "../custom-button/custom-button";
import firebase from "../../firebase/firebase.utils";

class AddItemsForm extends React.Component {
  constructor() {
    super();
    this.state = {
      Name: "",
      Email: "",
      Mobile: "",
      Location: "",
      imageURL: "",
      Image: "",
      Ideas: "",
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleSubmit = async (
    event,
    Name,
    Email,
    Mobile,
    Location,
    imageURL,
    Image,
    Ideas
  ) => {
    event.preventDefault();

    const name = this.state.Name;
    const imageUrl = this.state.imageURL;
    const email = this.state.Email;
    const mobile = this.state.Mobile;
    const location = this.state.Location;
    const image = this.state.Image;
    const ideas = this.state.Ideas;

    var db = firebase.firestore();

    const Items = db.collection("collections");
    Items.doc(email)
      .set(
        {
          name: name,
          imageUrl: imageUrl,
          email: email,
          mobile: mobile,
          location: location,
          image: image,
          ideas: ideas,
        },
        { merge: true }
      )
      .then(function () {
        console.log("Document successfully written!");
      })
      .catch(function (error) {
        console.error("Error writing document: ", error);
      });

    alert("updated successfully");
    this.setState({
      Name: "",
      imageURL: "",
      Price: "",
      Category: "select-main",
    });
    console.log(this.state);
  };

  handleChange = (event) => {
    const nam = event.target.name;
    const val = event.target.value;
    this.setState({ [nam]: val });
  };

  render() {
    return (
      <AddItemsContainer>
        <AddItemsTitle> Upload Items</AddItemsTitle>
        <span></span>
        <form onSubmit={this.handleSubmit}>
          <FormInput
            type="text"
            name="Name"
            value={this.state.Name}
            onChange={this.handleChange}
            label="Name"
            required
          />
          <FormInput
            type="email"
            name="Email"
            value={this.state.Email}
            onChange={this.handleChange}
            label="Email"
            required
          />
          <FormInput
            type="number"
            name="Mobile"
            value={this.state.Mobile}
            onChange={this.handleChange}
            label="Phone Number"
            required
          />
          <FormInput
            type="text"
            name="Location"
            value={this.state.Location}
            onChange={this.handleChange}
            label="Location"
            required
          />
          <FormInput
            type="text"
            name="imageURL"
            value={this.state.imageURL}
            onChange={this.handleChange}
            label="ImageURL"
          />
          <FormInput
            type="file"
            name="Image"
            value={this.state.Image}
            onChange={this.handleChange}
            label="Image"
          />
          <FormInput
            type="text"
            name="Ideas"
            value={this.state.Ideas}
            onChange={this.handleChange}
            label="Ideas"
          />

          <CustomButton type="submit">Add Item</CustomButton>
        </form>
      </AddItemsContainer>
    );
  }
}

export default AddItemsForm;
