import styled from "styled-components";

export const AddItemsContainer = styled.div`
  display: flex;
  flex-direction: column;
  width: 30%;
  margin-left: 35%;
  margin-top: 5%;
  margin-bottom: 10%;

  @media screen and (max-width: 800px) {
    width: 25%;
  }

  & select {
    width: 100%;
    margin: 4% auto 0;
    font-family: inherit;
    font-size: 18px;
    color: #60666d;
    cursor: pointer;
  }
`;

export const AddItemsTitle = styled.h2`
  margin: 10px 0;
`;
