import React from "react";
import { withRouter } from "react-router-dom";
import {
  AddItemsContainer,
  AddItemsTitle,
  LeftSection,
  RightSection,
  SectionsContainer,
  ButtonContainer,
  AddItemsText,
  FlagContainer,
} from "./add-items-form.styles";
import Russia from "../../assets/russia.png";
import Spain from "../../assets/spain.png";
import England from "../../assets/uk.png";
import FormInput from "../form-input/form-input";
import CustomButton from "../custom-button/custom-button";
import firebase from "../../firebase/firebase.utils";
import { storge } from "../../firebase/firebase.utils";

class AddItemsForm extends React.Component {
  constructor() {
    super();
    this.state = {
      Name: "",
      Email: "",
      Mobile: "",
      Location: "",
      imageURL: "",
      Image: "",
      uploadImage: "",
      Ideas: "",
      hidden: true,
      lang: "eng",
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleImageChange = this.handleImageChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.handleLang = this.handleLang.bind(this);
  }
  handleImageChange = (e) => {
    const image = e.target.files[0];
    this.setState({ hidden: false });
    const uploadTask = storge.ref(`images/${image.name}`).put(image);
    uploadTask.on(
      "state_changed",
      (snapshot) => {},
      (error) => {
        console.log(error);
      },
      () => {
        storge
          .ref("images")
          .child(image.name)
          .getDownloadURL()
          .then((url) => {
            this.setState({ uploadImage: url });
            console.log(this.state.uploadImage);
          });
      }
    );
  };
  handleSubmit = async (
    event,
    Name,
    Email,
    Mobile,
    Location,
    imageURL,
    Image,
    Ideas
  ) => {
    event.preventDefault();

    const name = this.state.Name;
    const imageUrl = this.state.imageURL;
    const email = this.state.Email;
    const mobile = this.state.Mobile;
    const location = this.state.Location;
    const image = this.state.uploadImage;
    const ideas = this.state.Ideas;

    var db = firebase.firestore();

    const Items = db.collection("collections");
    Items.doc(email)
      .set(
        {
          name: name,
          imageUrl: imageUrl,
          email: email,
          mobile: mobile,
          location: location,
          image: image,
          ideas: ideas,
        },
        { merge: true }
      )
      .then(function () {
        console.log("Document successfully written!");
      })
      .catch(function (error) {
        console.error("Error writing document: ", error);
      });

    alert("updated successfully");
    this.setState({
      Name: "",
      Email: "",
      Mobile: "",
      Location: "",
      imageURL: "",
      Image: "",
      uploadImage: "",
      Ideas: "",
      hidden: true,
    });

    console.log(this.state);
  };

  handleChange = (event) => {
    const nam = event.target.name;
    const val = event.target.value;
    this.setState({ [nam]: val });
  };
  handleClick = () => {
    document.getElementById("selectImage").click();
  };

  handleLang = (event) => {
    const val = event.target.alt;
    this.setState({ lang: val });
  };
  render() {
    return (
      <div>
        <FlagContainer>
          <img onClick={this.handleLang} value="eng" src={England} alt="eng" />
          <img onClick={this.handleLang} value="rus" src={Russia} alt="rus" />
          <img onClick={this.handleLang} value="spn" src={Spain} alt="spn" />
        </FlagContainer>
        <AddItemsContainer>
          {this.state.lang === "eng" ? (
            <div>
              <AddItemsTitle>
                What comes to your mind when you think about Dancing?
              </AddItemsTitle>
              <AddItemsText>
                Upload an image or write an idea .. something from your
                childhood, culture, imagination, fantasy, etc ..
